 */
let minimumTotal = function (triangle) {
    if (triangle.length <= 0) {
        return 0;
    }

    const dp = [triangle[0][0]];
    for (let i = 1; i < triangle.length; i++) {
        dp[i] = dp[i - 1] + triangle[i][i];
        for (let j = i - 1; j >= 1; j--) {
            dp[j] = Math.min(dp[j], dp[j - 1]) + triangle[i][j];
        }
        dp[0] += triangle[i][0];
    }
    return Math.min(...dp);
};

