    const len = prices.length;
    if (len <= 1) {
        return 0;
    }

    const dp = [0];

    let min = prices[0];
    for (let i = 1; i < len; i++) {
        dp[i] = Math.max(dp[i - 1], prices[i] - min);
        min = Math.min(prices[i], min);
    }

    let p2 = 0;
    let max = prices[len - 1];
    for (let i = len - 2; i >= 0; i--) {
        max = Math.max(prices[i], max);
        p2 = Math.max(p2, max - prices[i]);
        dp[i] += p2;
    }

    return Math.max(...dp);
};

