/**
 * @param {string} s
 * @return {boolean}
 */
let isPalindrome = function (s) {
    for (let i = 0, j = s.length - 1; i < j; i++, j--) {
        let left = s.charCodeAt(i);
        while (i < j && (left < 48 || (left > 57 && left < 65) || (left > 90 && left < 97) || left > 122)) {
            left = s.charCodeAt(++i);
        }
        if (i >= j) {
            return true;
        }
        if (left >= 65 && left <= 90) {
            left += 32;
        }

        let right = s.charCodeAt(j);
        while (i < j && (right < 48 || (right > 57 && right < 65) || (right > 90 && right < 97) || right > 122)) {
            right = s.charCodeAt(--j);
        }
        if (i >= j) {
            return true;
        }
        if (right >= 65 && right <= 90) {
            right += 32;
        }

        if (left !== right) {
            return false;
        }
    }

    return true;
};

/**
 * @param {string} beginWord
 * @param {string} endWord
 * @param {string[]} wordList
 * @return {string[][]}
 */
function findLadders(beginWord, endWord, wordList) {
    wordList = new Set(wordList);
    if (!wordList.has(endWord)) {
        return [];
    }

    const ALPHABET = 'abcdefghijklmnopqrstuvwxyz';

    const result = [];
    let isEndWordFound = false;
    const levelWords = new Set();
    const queue = [[beginWord, null], null];
    while (queue.length > 1) {
        const node = queue.shift();

        if (node === null) {
            if (isEndWordFound) {
                break;
            }
            levelWords.forEach((word) => wordList.delete(word));
            levelWords.clear();
            queue.push(null);
            continue;
        }

        const word = node[0];

        for (let i = word.length - 1; i >= 0; i--) {
            const head = word.slice(0, i);
            const tail = word.slice(i + 1);

            for (let c = 0; c < 26; c++) {
                if (ALPHABET[c] !== word[i]) {
                    const w = head + ALPHABET[c] + tail;
                    if (w === endWord) {
                        const path = [endWord];
                        for (let n = node; n !== null; n = n[1]) {
                            path.unshift(n[0]);
                        }
                        result.push(path);
                        isEndWordFound = true;
                    }
                    if (wordList.has(w)) {
                        levelWords.add(w);
                        queue.push([w, node]);
                    }
                }
            }
        }
    }

    return result;
}

/**
 * @param {string} beginWord
 * @param {string} endWord
 * @param {string[]} wordList
 * @return {number}
 */
let ladderLength = function (beginWord, endWord, wordList) {
    wordList = new Set(wordList);
    if (!wordList.has(endWord)) {
        return 0;
    }

    const ALPHABET = 'abcdefghijklmnopqrstuvwxyz';

    let level = 1;
    const queue = [beginWord, null];
    while (queue.length > 1) {
        const word = queue.shift();

        if (word === null) {
            level++;
            queue.push(null);
            continue;
        }

        for (let i = word.length - 1; i >= 0; i--) {
            const head = word.slice(0, i);
            const tail = word.slice(i + 1);

            for (let c = 0; c < 26; c++) {
                if (ALPHABET[c] !== word[i]) {
                    const word = head + ALPHABET[c] + tail;
                    if (word === endWord) {
                        return level + 1;
                    }
                    if (wordList.delete(word)) {
                        queue.push(word);
                    }
                }
            }
        }
    }

    return 0;
};

/**
 * @param {number[]} nums
 * @return {number}
 */
let longestConsecutive = function (nums) {
    const numSet = new Set(nums);
    let maxCount = 0;
    while (numSet.size > 0) {
        const num = numSet.values().next().value;
        numSet.delete(num);
        let count = 1;
        for (let n = num + 1; numSet.delete(n); n++) {
            count++;
        }
        for (let n = num - 1; numSet.delete(n); n--) {
            count++;
        }
        if (count > maxCount) {
            maxCount = count;
        }
    }
    return maxCount;
};

