/**
 * @param {character[][]} board
 * @return {void} Do not return anything, modify board in-place instead.
 */
let solve = function (board) {
    const height = board.length;
    if (height <= 1) {
        return;
    }
    const width = board[0].length;
    if (width <= 1) {
        return;
    }

    const rowend = height - 1;
    const colend = width - 1;

    const queue = [];

    for (let row = 0; row < height; row++) {
        if (board[row][0] === 'O') {
            board[row][0] = '#';
            queue.push(row, 0);
        }
        if (board[row][colend] === 'O') {
            board[row][colend] = '#';
            queue.push(row, colend);
        }
    }

    for (let col = 0; col < width; col++) {
        if (board[0][col] === 'O') {
            board[0][col] = '#';
            queue.push(0, col);
        }
        if (board[rowend][col] === 'O') {
            board[rowend][col] = '#';
            queue.push(rowend, col);
        }
    }

    while (queue.length > 0) {
        const row = queue.shift();
        const col = queue.shift();
        if (row < rowend && board[row + 1][col] === 'O') {
            board[row + 1][col] = '#';
            queue.push(row + 1, col);
        }
        if (row > 0 && board[row - 1][col] === 'O') {
            board[row - 1][col] = '#';
            queue.push(row - 1, col);
        }
        if (board[row][col + 1] === 'O') {
            board[row][col + 1] = '#';
            queue.push(row, col + 1);
        }
        if (board[row][col - 1] === 'O') {
            board[row][col - 1] = '#';
            queue.push(row, col - 1);
        }
    }

    for (let row = 0; row < height; row++) {
        for (let col = 0; col < width; col++) {
            if (board[row][col] === '#') {
                board[row][col] = 'O';
            } else if (board[row][col] === 'O') {
                board[row][col] = 'X';
            }
        }
    }
};

/**
 * Definition for undirected graph.
 * function UndirectedGraphNode(label) {
 *     this.label = label;
 *     this.neighbors = [];   // Array of UndirectedGraphNode
 * }
 */

/**
 * @param {UndirectedGraphNode} graph
 * @return {UndirectedGraphNode}
 */
let cloneGraph = function (graph) {
    const cache = {};
    return _clone(graph);

    function _clone(graph) {
        if (!graph) {
            return graph;
        }
        const label = graph.label;
        if (!cache[label]) {
            cache[label] = new UndirectedGraphNode(label);
            cache[label].neighbors = graph.neighbors.map(_clone);
        }
        return cache[label];
    }
};

/**
 * Definition for a binary tree node.
 * function TreeNode(val) {
 *     this.val = val;
 *     this.left = this.right = null;
 * }
 */
/**
 * @param {TreeNode} root
 * @return {TreeNode}
 */
const upsideDownBinaryTree = function (root) {
    let curr = root;
    let next = null;
    let temp = null;
    let prev = null;
    while (curr !== null) {
        next = curr.left;
        curr.left = temp;
        temp = curr.right;
        curr.right = prev;
        prev = curr;
        curr = next;
    }
    return prev;
};

